/// <reference path="../types.d.ts" />

/**
 * Checks if the application is running as an installed PWA and prompts the user to install if not.
 * Provides specific instructions for Safari users.
 */
export function promptInstall(): boolean {
    if (window.matchMedia('(display-mode: standalone)').matches) {
        console.log('Running as a standalone app.');
        return false;
    }

    // Check if we are on iOS (Safari)
    const isIOS =
        /iPad|iPhone|iPod/.test(navigator.userAgent) &&
        !(window as any).MSStream;

    if (isIOS) {
        // Display HTML instructions for Safari
        const installInstructionsHTML = `
      <div id="install-prompt-ios" style="font-size: 2.4rem; background: #fff; border-radius: 6px; position: fixed; top: 50%; left: 50%; transform: translate(-50%, -50%); width: 85%; padding: 4rem; text-align: center; z-index: 999999;">
        <img width="64" style="float:left;" src="data:image/png;base64," />
        ${
            APP_METADATA?.name || 'This game'
        } is best experienced by adding to your homescreen first.
        <ol style="margin-top: 3rem; margin-bottom: 0; padding: 0; text-align: left; padding-left: 4rem; font-size: 2.1rem;">
          <li>Tap the <svg xmlns="http://www.w3.org/2000/svg" style="position: relative; top: .75rem;" fill="#000000" width="3rem" height="3rem" viewBox="0 0 50 50"><path d="M30.3 13.7L25 8.4l-5.3 5.3-1.4-1.4L25 5.6l6.7 6.7z"/><path d="M24 7h2v21h-2z"/><path d="M35 40H15c-1.7 0-3-1.3-3-3V19c0-1.7 1.3-3 3-3h7v2h-7c-.6 0-1 .4-1 1v18c0 .6.4 1 1 1h20c.6 0 1-.4 1-1V19c0-.6-.4-1-1-1h-7v-2h7c1.7 0 3 1.3 3 3v18c0 1.7-1.3 3-3 3z"/></svg><b>Share</b> button at the bottom of the screen.</li>
          <li>Select <b>Add to Home Screen</b>.</li>
        </ol>
      </div>
    `;
        document.body.insertAdjacentHTML('beforeend', installInstructionsHTML);

        return true;
    } else {
        // For Android, listen for the beforeinstallprompt event
        window.addEventListener(
            'beforeinstallprompt',
            (event: BeforeInstallPromptEvent) => {
                // Prevent the browser's default install prompt
                event.preventDefault();
                const deferredPrompt = event;

                // Optionally, you can show a custom install button or UI here
                console.log('Ready to install the app!');

                // For demonstration purposes, let's automatically trigger the prompt after a short delay
                setTimeout(async () => {
                    if (deferredPrompt) {
                        deferredPrompt.prompt();
                        const { outcome } = await deferredPrompt.userChoice;
                        console.log(
                            `User response to the install prompt: ${outcome}`
                        );
                        // We've used the prompt, and can't use it again directly, so nullify it
                        (deferredPrompt as any) = null;
                    }
                }, 3000); // Adjust the delay as needed
            }
        );
    }

    return false;
}

interface BeforeInstallPromptEvent extends Event {
    readonly platforms: string[];
    readonly userChoice: Promise<{
        outcome: 'accepted' | 'dismissed';
        platform: string;
    }>;
    prompt(): Promise<void>;
}
